var mongoose = require("mongoose");

var Schema = mongoose.Schema;

var bountySchema = new Schema({
  firstName: {
    type: String,
    required: true
  },
  lastName: {
    type: String,
    required: true
  },
  living: {
    type: Boolean,
    required: true
  },
  bountyAmt: {
    type: Number,
    required: true
  },
  type: {
    type: String,
    required: true
  }
});

module.exports = {
  Bounty: mongoose.model("bounty", bountySchema)
};
